import React, { Component } from 'react'
import { 
    Card, 
    CardImg, 
    CardText, 
    CardBody,
    CardTitle, 
    CardSubtitle, 
    // Button, 
    Input, 
    InputGroup, 
    // Collapse, 
    // Navbar, 
    // NavbarToggler, 
    // NavbarBrand, 
    // Nav, 
    // NavItem, 
    // NavLink, 
    Container, 
    Row, 
    Col, 
    Jumbotron 
    } from 'reactstrap';
import Papa from 'papaparse'
import 'bootstrap/dist/css/bootstrap.min.css'
import './App.css'

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {data: [] };   
        this.handleChange = this.handleChange.bind(this);
        this.updateData = this.updateData.bind(this)
    }

    handleChange(event) {
        event.preventDefault()
        const inventory = event.target.files[0]
        Papa.parse(inventory, {
            header: true,
            complete: this.updateData
        })
    } // END

    updateData(results) {
        const data = results.data
        console.log(data)
        this.setState({data}) // I have it in state. How to get it in UI?
    }

    renderData() {
        return  this.state.data.length > 1 
           ? this.state.data.map((item) => (
               // this is where to put the Card
               <Col key={item.sku} > {item.sku} </Col>
           )) 
           : null

    }
    render() {
        return (
            <div>
                <Jumbotron>
                    <form >
                        <InputGroup>
                            Name:
                            <Input type="file" onChange={this.handleChange} />
                        </InputGroup>
                    </form>
                </Jumbotron>

                <Container>
                    <Row>
                        <Card>
                            <CardImg top width="100%" src="https://circaskin.circalighting.com/media/catalog/product/cache/1/small_image/300x300/9df78eab33525d08d6e5fb8d27136e95/n/w/nw2005rscg_5.png" />
                            <CardBody>
                                <CardTitle>Title</CardTitle>
                                <CardSubtitle> {this.renderData()} </CardSubtitle>
                                <CardText>Lorem ipsum</CardText>
                            </CardBody>
                        </Card>
                    </Row>

                    {/* <Row>
                        <div className="album"> {this.renderData()} </div> 
                    </Row> */}
                </Container>
            </div>          
        );
    }

} // END

export default App
