class ProductUpload extends React.Component {

  uploadHandler = (event) => {
    const inventory = event.target.files[0]

    Papa.parse(inventory, {
      header: true,
      complete: function(results) {  

        let feed = results.data;   
        console.log(feed)
        // I have the object but I need to be able to export it
        return
      }
    })
  } // END UploadHandler

  render() {


    return (
      <div>
        <InputGroup>
          <Input 
          type="file" 
          name="inputCSV" 
          onChange={uploadHandler}
          />
        </InputGroup>
      </div>
    )
  }
}


