// Stateless component
const ProductUpload = () => {

    // Upload handler
  const uploadHandler = (event) => {
    const inventory = event.target.files[0]

    Papa.parse(inventory, {
      header: true,
      complete: function(results) {  

        let items = results.data;   
        console.log(items)
        // I have the object but I need to be able to export it
      }
    })
  } // END UploadHandler

  return (
    <div>
      <InputGroup>
        <Input 
        type="file" 
        name="inputCSV" 
        onChange={uploadHandler}
        />
      </InputGroup>
    </div>
  )

} // END ProductUpload



