import React from 'react'
import { Input, InputGroup } from 'reactstrap'
import Papa from 'papaparse'

// NOTE: This component shows the input, handles the upload, and exports the product object

// Here are some inspiring comments from https://react.org 
// "We recommend naming props from the component’s own point of view rather than the context in which it is being used."
// "All React components must act like pure functions with respect to their props."


// Imports the product feed

const ProductUpload = () => {

  const ProductInput = () => {
    return (
      <div>
        <InputGroup>
          <Input 
          type="file" 
          name="inputCSV" 
          onChange={uploadHandler}
          />
        </InputGroup>
      </div>
    )
  } // END ProductInput


  // Upload handler
const uploadHandler = (event) => {
  const inventory = event.target.files[0]

  Papa.parse(inventory, {
    header: true,
    complete: function(results) {  

      let feed = results.data;   
      console.log(feed)
      // I have the object but I need to be able to export it
    }
  })
} // END UploadHandler
}





